mapper.update:
UPDATE boardtb

	hit = hit + 1,


	AND board_no = #{boardNo}

mapper.select:
SELECT   board_no, title, location, post_date, hit, content, delete_status, user_id, board_cate, (SELECT user_nick FROM userTB WHERE user_id=boardtb.user_id)   
FROM boardtb   
WHERE board_cate = 4 AND board_no = #{boardNo}
