mapper.select:
SELECT count(*) FROM boardtb

	AND board_cate = #{category }
	AND title LIKE '%' || (#{search }) || '%'

SELECT * FROM (
SELECT rownum rnum, R.* FROM (


		AND board_cate = #{category }
		AND title LIKE '%' || (#{search }) || '%'

	ORDER BY board_no DESC
) R )

	AND rnum BETWEEN #{startNo } AND #{endNo }
